package com.lqqqqf.domain.pojo;import org.springframework.security.core.GrantedAuthority;import javax.persistence.Entity;import javax.persistence.JoinColumn;import javax.persistence.JoinTable;import javax.persistence.ManyToMany;import java.util.Set;/** * Created by apple on 16/6/13. */@Entitypublic class Permission extends BaseEntity implements GrantedAuthority {    private String authority;    private Set<Role> roles;    public void setAuthority(String authority) {        this.authority = authority;    }    @Override    public String getAuthority() {        return this.authority;    }    @ManyToMany    @JoinTable(name = "role_permission",            joinColumns = @JoinColumn(name = "perm_id"),            inverseJoinColumns = @JoinColumn(name = "role_id"))    public Set<Role> getRoles() {        return roles;    }    public void setRoles(Set<Role> roles) {        this.roles = roles;    }}